mapper.select:
SELECT p.*, NVL(t.tran_status_code,'000') tranCode  		
FROM product p, transaction t 
WHERE p.prod_no=t.prod_no(+) AND p.prod_no = #{value}

mapper.update:
UPDATE product
prod_no = #{prodNo}, prod_name = #{prodName} , prod_detail = #{prodDetail}, manufacture_day = #{manuDate} , price = #{price}, image_file = #{fileName}
WHERE prod_no = #{prodNo}
