mapper.select:
SELECT prod_no, buyer_id, payment_option, receiver_name, receiver_phone, demailaddr, dlvy_request, dlvy_date, order_data, tran_status_code, tran_no   		
FROM transaction
WHERE tran_no = #{value}

mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName} , receiver_phone = #{receiverPhone}, demailaddr = #{divyAddr}, dlvy_request = #{divyRequest}, dlvy_date= #{divyDate}
WHERE tran_no = #{tranNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.prod_name, t.* FROM product p, transaction t
p.prod_no=t.prod_no(+)
AND t.tran_no IS NOT NULL
AND t.buyer_id=#{buyerId}
ORDER BY t.tran_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
